var trigger = require("../../lib/events/trigger");

var el = document.createElement("div");
var el2 = document.createElement("span");
var els = [el, el2];

var classCb = function() {
  this.className += "on";
};
var attrCb = function() {
  this.setAttribute("data-state", this.getAttribute("data-state") + "ON");
};

