  t.end();
});

tape("test events on/off/trigger for multiple elements, one event", function(
  t
) {
  el.className = "";
  el2.className = "";

  on(els, "click", classCb);
  trigger(els, "click");
  t.equal(
    el.className,
    "on",
    "attached callback has been fired properly on the first element"
