  trigger(form, "submit");
  t.equal(
    form.getAttribute(attr),
    "anchor-empty",
    "empty anchor stop behavior"
  );

  form.action = window.location.href;
  trigger(form, "submit");
  t.equal(
    form.getAttribute(attr),
    "reload",
