  t.equal(loadUrlCalled, true, "loadUrl() called correctly");

  loadUrlCalled = false;
  form.setAttribute(attr, "");
  form.action =
    window.location.protocol + "//" + window.location.host + "/internal";
  form.method = "GET";
  trigger(form, "submit");
  t.equal(
    form.getAttribute(attr),
    "submit",
    "triggering a GET request to the next page"
  );
  t.equal(loadUrlCalled, true, "loadUrl() called correctly");

