  var loadUrl = function() {};

  attachForm.call({ options: options, loadUrl: loadUrl }, form);

  form.action =
    window.location.protocol +
    "//" +
    window.location.host +
    window.location.pathname +
    window.location.search;
  form.method = "GET";
  trigger(form, "submit");

